import { useContext, useState, useEffect } from "react";
import Head from "next/head";
import AppContext from "../components/context";
import Home from "./index"
import Layout from "../components/layout"
import Cookie from "js-cookie"
import axios from 'axios';



function MyApp(props){
  var {cart,addItem,removeItem} = useContext(AppContext)
  const [state,setState] = useState({cart:cart});
  const { Component, pageProps } = props;


  const [user, setUser] = useState(null);

  // Check if user is logged in on mount
  useEffect(() => {
    checkLoginStatus();
  }, []);

  // Login function
  async function login(username, password) {
    try {
      const response = await axios.post('http://localhost:1337/auth/local', {
        identifier: username,
        password,
      });
      const { data } = response;
      setUser(data.user);
      localStorage.setItem('token', data.jwt);
    } catch (error) {
      console.error(error);
    }
  }

  // Logout function
  function logout() {
    setUser(null);
    localStorage.removeItem('token');
  }

  // Check login status function
  async function checkLoginStatus() {
    const token = localStorage.getItem('token');
    if (!token) {
      setUser(null);
      return;
    }

    try {
      const response = await axios.get('http://localhost:1337/users/me', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const { data } = response;
      setUser(data);
    } catch (error) {
      console.error(error);
    }
  }

  // Handle form submit for login
  function handleSubmit(event) {
    event.preventDefault();
    const { username, password } = event.target.elements;
    login(username.value, password.value);
  }

  addItem = (item) => {
    let { items } = state.cart;
    //check for item already in cart
    //if not in cart, add item if item is found increase quanity ++
    let foundItem = true;
    if(items.length > 0){
      foundItem = items.find((i) => i.id === item.id);

      if(!foundItem) foundItem = false;
    }
    else{
      foundItem = false;
    }
    console.log(`Found Item value: ${JSON.stringify(foundItem)}`)
    // if item is not new, add to cart, set quantity to 1
    if (!foundItem) {
      //set quantity property to 1

      let temp = JSON.parse(JSON.stringify(item));
      temp.quantity = 1;
      var newCart = {
          items: [...state.cart.items,temp],
          total: state.cart.total + item.price,
      }
      setState({cart:newCart})
      console.log(`Total items: ${JSON.stringify(newCart)}`)
    } else {
      // we already have it so just increase quantity ++
      console.log(`Total so far:  ${state.cart.total}`)
      newCart= {
          items: items.map((item) =>{
            if(item.id === foundItem.id){
              return Object.assign({}, item, { quantity: item.quantity + 1 })
             }else{
            return item;
          }}),
          total: state.cart.total + item.price,
        }
    }
    setState({cart: newCart});  // problem is this is not updated yet
    console.log(`state reset to cart:${JSON.stringify(state)}`)

  };
  removeItem = (item) => {
    let { items } = state.cart;
    //check for item already in cart
    const foundItem = items.find((i) => i.id === item.id);
    if (foundItem.quantity > 1) {
      var newCart = {
        items: items.map((item) =>{
        if(item.id === foundItem.id){
          return Object.assign({}, item, { quantity: item.quantity - 1 })
         }else{
        return item;
      }}),
      total: state.cart.total - item.price,
      }
      //console.log(`NewCart after remove: ${JSON.stringify(newCart)}`)
    } else { // only 1 in the cart so remove the whole item
      console.log(`Try remove item ${JSON.stringify(foundItem)}`)
      const index = items.findIndex((i) => i.id === foundItem.id);
      items.splice(index, 1);
      var newCart= { items: items, total: state.cart.total - item.price } 
    }
    setState({cart:newCart});
  }

  return (
    <AppContext.Provider value={{
      cart: state.cart, addItem: addItem, removeItem: removeItem,isAuthenticated:false,user:null,setUser:(user)=>{this.setUser({user})}}}>
      <Head>
        <link
          rel="stylesheet"
          href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css"
          integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm"
          crossOrigin="anonymous"
        />
      </Head>
      <div>
      <nav>
        <ul>
          <li><a href="/">Home</a></li>
          {user ? (
            <li>Hello, {user.username} <button onClick={logout}>Logout</button></li>
          ) : (
            <li>
              <form onSubmit={handleSubmit}>
                <input type="text" name="username" placeholder="Username" />
                <input type="password" name="password" placeholder="Password" />
                <button type="submit">Login</button>
              </form>
            </li>
          )}
        </ul>
      </nav>
      <main>
      <Layout>
          <Component {...pageProps} />
      </Layout>
      </main>
    </div>


    </AppContext.Provider>
  );

}


export default MyApp;
