import {useRouter} from "next/router"
import {gql,useQuery} from '@apollo/client';
import {useState, useContext} from 'react'
import AppContext from "./context"
import { InputGroup, InputGroupAddon,Input} from "reactstrap";
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardText,
  CardTitle,
  Row,
  Col} from "reactstrap";
async function Dishes({restId},props){
  const [restaurantID, setRestaurantID] = useState()
  const {addItem} = useContext(AppContext)
  const [query, setQuery] = useState("");
  const[search, setSearch] = useState("");

const GET_RESTAURANT_DISHES = gql`
  query($id: ID!) {
    restaurant(id: $id)(filters:dishes{name: {contains: $res.name}}) {
      id
      name
      description
      image {
        url
      }
      dishes{
      id
      name
      description
      price
      image {
        url
      }
      }
    }
  }
`;

//console.log(`restaurant = ${restaurantID}`);
  const router = useRouter();

 // GET /api/restaurants?filters\[dishes\][name][$contains]=guac
  const { loading, error, data } = useQuery(GET_RESTAURANT_DISHES, {
    variables: { id: restId},
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p>ERROR here</p>;
  if (!data) return <p>Not found</p>;

  // GET /api/restaurants?filters\[authors\][hobby][$contains]=dance
  let restaurant = data.restaurant;
  let dishsearchQuery = data.restaurant.dishes.filter((res) => {
    console.log("res on dishes", res.name)
    return res.name.toLowerCase().includes(props.search)
  }) ||[];
console.log("searchQuery on Dishes: ", dishsearchQuery)

//console.log("understanding dish structure: ", restaurant.dishes)
  if (restId > 0){
//console.log(data);
    return (
      <>
              <div className="search">
              <h2> Dish Search</h2>
                <InputGroup >
                <InputGroupAddon addonType="append"> Search </InputGroupAddon>
                <Input
                    onChange={(e) =>
                    setQuery(e.target.value.toLocaleLowerCase())
                    }
                    value={query}
                />
                </InputGroup><br></br>
            </div>
            <Dishes search={query} />
          {dishsearchQuery.map((res) => (
            <Col xs="6" sm="4" style={{ padding: 0 }} key={res.id}>
              <Card style={{ margin: "0 10px" }}>
                <CardImg
                  top={true}
                  style={{ height: 150, width:150 }}
                  src={`http://localhost:1337${res.image.url}`}
                />
                <CardBody>
                  <CardTitle>{res.name}</CardTitle>
                  <CardText>{res.description}</CardText>
                </CardBody>
                <div className="card-footer">
                  <Button color="info"
                    outline
                    onClick = {()=> addItem(res)}
                  >
                    + Add To Cart
                  </Button>

                </div>
              </Card>
            </Col>
          ))}
        </>
        )}
        else{
          return <h1> No Dishes</h1>
        }
    }
    export default Dishes
