import {useRouter} from "next/router"
import {gql,useQuery} from '@apollo/client';
import {useState, useContext} from 'react'
import AppContext from "./context"
import { InputGroup, InputGroupAddon,Input} from "reactstrap";
import {ApolloProvider,ApolloClient,HttpLink, InMemoryCache} from '@apollo/client';
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardText,
  CardTitle,
  Row,
  Col} from "reactstrap";
function Dishes({restId}){
  const API_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:1337";
  const cache = new InMemoryCache()
  const [restaurantID, setRestaurantID] = useState()
  const {addItem} = useContext(AppContext)
  const [query, setQuery] = useState("");
  const link = new HttpLink({ uri: `${API_URL}/graphql`})
  const client = new ApolloClient({link,cache});


const GET_RESTAURANT_DISHES = gql`
  query($id: ID!) {
    restaurant(id: $id) {
      id
      name
      dishes {
        id
        name
        description
        price
        image {
          url
        }
      }
    }
  }
`;
//console.log(`restaurant = ${restaurantID}`);
  const router = useRouter();

  const { loading, error, data } = useQuery(GET_RESTAURANT_DISHES, {
    variables: { id: restId},
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p>ERROR here</p>;
  if (!data) return <p>Not found</p>;

  let restaurant = data.restaurant;
  let dishsearchQuery = data.restaurant.dishes.filter((res) => {
    console.log("dishes res:", res.name.toLowerCase())
    return res.name.toLowerCase().includes(props.search)
  }) ||[];
console.log("dishsearchQuery on Dishes: ", dishsearchQuery)
//console.log("understanding dish structure: ", restaurant.dishes)
  if (restId > 0){
    if (dishsearchQuery.length > 0) {
      const dishList = dishsearchQuery.map((res) => (

            <Col xs="6" sm="4" style={{ padding: 0 }} key={res.id}>
              <Card style={{ margin: "0 10px" }}>
                <CardImg
                  top={true}
                  style={{ height: 150, width:150 }}
                  src={`http://localhost:1337${res.image.url}`}
                />
                <CardBody>
                  <CardTitle>{res.name}</CardTitle>
                  <CardText>{res.description}</CardText>
                </CardBody>
                <div className="card-footer">
                  <Button color="info"
                    outline
                    onClick = {()=> addItem(res)}
                  >
                    + Add To Cart
                  </Button>

                </div>
              </Card>
            </Col>
          ))
        return (
          <ApolloProvider client={client}>
          <div className="search">
          <h2> Dish Search</h2>
            <InputGroup >
            <InputGroupAddon addonType="append"> Search </InputGroupAddon>
            <Input
                onChange={(e) =>
                setQuery(e.target.value.toLocaleLowerCase())
                }
                value={query}
            />
            </InputGroup><br></br>
        </div>
        <Dishes search={query} />

         <Container>
         <Row xs='3'>
           {restList}
         </Row>
         </Container>
         </ApolloProvider>
        )}
        else(
          <h1>No Dishes</h1>

          )
        }
        else{
          return <h1> Select a restaurant to see dishes</h1>
        }
    }
    export default Dishes
