import {useRouter} from "next/router"
import {gql,useQuery} from '@apollo/client';
import {useState, useContext} from 'react'
import AppContext from "./context"
import { InputGroup, InputGroupAddon,Input} from "reactstrap";
import {
  Button,
  Card,
  CardBody,
  CardImg,
  CardText,
  CardTitle,
  Row,
  Col} from "reactstrap";
function Dishes({restId}, props){
  const [restaurantID, setRestaurantID] = useState()
  const {addItem} = useContext(AppContext)
  const [query, setQuery] = useState("");

const GET_RESTAURANT_DISHES = gql`
  query($id: ID!) {
    restaurant(id: $id) {
      id
      name
      dishes {
        id
        name
        description
        price
        image {
          url
        }
      }
    }
  }
`;
//console.log(`restaurant = ${restaurantID}`);
  const router = useRouter();

  const { loading, error, data } = useQuery(GET_RESTAURANT_DISHES, {
    variables: { id: restId},
  });

  if (loading) return <p>Loading...</p>;
  if (error) return <p>ERROR here</p>;
  if (!data) return <p>Not found</p>;

  let restaurant = data.restaurant;

//console.log("understanding dish structure: ", restaurant.dishes)
  if (restId > 0){
    console.log("restId on Dishes: ", data)
    let searchQuery = data.restaurant.dishes.filter((res) => {
      return res.name.toLowerCase().includes(props.search)
    }) || [];

    let dishId = searchQuery[0] ? searchQuery[0].id : null;


//console.log(data);
if (restId >0){
      if (searchQuery.length > 0) {
        const dishList = searchQuery.map((res) => (
          <Col xs="6" sm="4" style={{ padding: 0 }} key={res.id}>
          <Card style={{ margin: "0 10px" }}>
            <CardImg
              top={true}
              style={{ height: 150, width:150 }}
              src={`http://localhost:1337${res.image.url}`}
            />
            <CardBody>
              <CardTitle>{res.name}</CardTitle>
              <CardText>{res.description}</CardText>
            </CardBody>
            <div className="card-footer">
              <Button color="info"
                outline
                onClick = {()=> addItem(res)}
              >
                + Add To Cart
              </Button>

            </div>
          </Card>
        </Col>
        ))
      return(
        <>
              <div className="search">
              <h2> Dish Search</h2>
                <InputGroup >
                <InputGroupAddon addonType="append"> Search </InputGroupAddon>
                <Input
                    onChange={(e) =>
                    setQuery(e.target.value.toLocaleLowerCase())
                    }
                    value={query}
                />
                </InputGroup><br></br>
            </div>
            <Dishes search={query} />
            <Row xs='3'>
          {dishList}
        </Row>
        </>
        )}
        else{
          const dishList = restaurant.dishes.map((res) => (
            <Col xs="6" sm="4" style={{ padding: 0 }} key={res.id}>
            <Card style={{ margin: "0 10px" }}>
              <CardImg
                top={true}
                style={{ height: 150, width:150 }}
                src={`http://localhost:1337${res.image.url}`}
              />
              <CardBody>
                <CardTitle>{res.name}</CardTitle>
                <CardText>{res.description}</CardText>
              </CardBody>
              <div className="card-footer">
                <Button color="info"
                  outline
                  onClick = {()=> addItem(res)}
                >
                  + Add To Cart
                </Button>

              </div>
            </Card>
          </Col>
          ))
        return(
          <>
                <div className="search">
                <h2> Dish Search</h2>
                  <InputGroup >
                  <InputGroupAddon addonType="append"> Search </InputGroupAddon>
                  <Input
                      onChange={(e) =>
                      setQuery(e.target.value.toLocaleLowerCase())
                      }
                      value={query}
                  />
                  </InputGroup><br></br>
              </div>
              <Dishes search={query} />
              <Row xs='3'>
          {dishList}
        </Row>
          </>
          )}}
          else{
            <h1>No Dishes Found</h1>
          }
        }
    }
    export default Dishes
